app.directive("helloWorld",function(){return{restrict:"AE",replace:!0,template:'<p style="background-color:{{color}}">Type a color (word or hex code) in the box to see what happens. I did this while learning how to build Angular directives</p>',link:function(a,b,c){b.bind("click",function(){b.css("background-color","gold"),a.$apply(function(){a.color="gold"})}),b.bind("mouseover",function(){b.css("cursor","pointer")})}}}),app.directive("shakeMe",function(){return{restrict:"AEXC",replace:!0,template:'<div class="{{class}}"><h3>Click Me!</h3></div>',link:function(a,b,c){b.bind("click",function(){b.css("class","animated shake"),a.$apply(function(){a["class"]="animated shake"})}),b.bind("mouseover",function(){b.css("cursor","pointer")})}}});