app.directive("helloWorld",function(){return{restrict:"AE",replace:!0,template:'<p style="background-color:{{color}}">Type a color (word or hex code) in the box to see what happens. I did this while learning how to build Angular directives</p>',link:function(a,b,c){b.bind("click",function(){b.css("background-color","white"),a.$apply(function(){a.color="white"})}),b.bind("mouseover",function(){b.css("cursor","pointer")})}}});